//for city not found
function handleError() {
  innermodal.innerHTML = `<img src="data:image/svg+xml;base64,"></img>
    CITY NOT FOUND!</div>`;
  model.classList.add("open");
}


function api() {
  const place = document.getElementById("idSearch").value;
  fetch(
    `https://api.openweathermap.org/data/2.5/weather?q=${place}&units=metric&appid=${API_KEY}`) 
 
    .then((res) => res.json())
    .then((data) => {
      const error = data.message;
      if (error === "city not found") {
        handleError();
      } else {
        weatherData(data);
      }
    });}

//click,enter search bar

placeOf.addEventListener("keyup", function (event) {
  event.preventDefault();
  if (event.keyCode === 13) {
    api();
  }
});

document.getElementById("btn").addEventListener("click", function (event) {
  api();
});

//closing for pop up error
closeBtn.addEventListener("click", function (event) {
  model.classList.remove("open");
});
